(function() {
  var RedirectRouter;

  RedirectRouter = (function() {
    function RedirectRouter() {}

    RedirectRouter.route = function(query, callback) {
      return query.graph.node_manager.find_node(query.current_router.configuration, function(node) {
        query.node = node;
        return query.graph.query(query, callback);
      });
    };

    return RedirectRouter;

  })();

  module.exports = RedirectRouter;

}).call(this);
