(function() {
  'use strict';
  var ConfigurationManager, conf, should;

  should = require('should');

  ConfigurationManager = require('../../lib/configuration_manager');

  conf = {
    "database": "mongo",
    "name": "pirati"
  };

  describe('ConfigurationManager', function() {
    return describe('new', function() {
      it('new', function(done) {
        var cm;
        cm = new ConfigurationManager(conf);
        cm.should.be["instanceof"](ConfigurationManager);
        cm.should.have.property('configuration', conf);
        return done();
      });
      return it('get_configuration', function(done) {
        var cm;
        cm = new ConfigurationManager(conf);
        should(cm.get_configuration()).equal(conf);
        return done();
      });
    });
  });

}).call(this);
