(function() {
  'use strict';
  var EchoRouter, Query, should;

  should = require('should');

  EchoRouter = require('../../lib/echo_router');

  Query = require('../../lib/query');

  describe('EchoRouter', function() {
    return describe('route', function() {
      return it('should return routed query', function(done) {
        var query;
        query = new Query('testnode', 'create', '{ "test": "value" }');
        return EchoRouter.route(query, function(returned_query) {
          returned_query.should.equal(query);
          return done();
        });
      });
    });
  });

}).call(this);
